example.code:
IntDict inventory;

void setup() {
  size(200, 200);
  inventory = new IntDict();
  inventory.set("cd",84);
  inventory.set("tapes",15);
  inventory.set("records",102);
  println(inventory);  // There are 84 cds
  inventory.add("cds", 20);
  println(inventory);  // There are 104 cds
}
